  }
  getSkills(): any {
    for (let i = 0; i < this.categories.length; i++) {

      this.matriceDeCompetencesService.getSkills(this.categories[i].name).subscribe(
        (res) => {
          this.categories[i].skills = res
          console.log("lol" + JSON.stringify(this.categories))
          // for (let j = 0; j < this.skills.length; j++) {

          //   this.matriceDeCompetencesService.getNotes(this.categories[i].skills[j].name, this.Perimetre, this.Pole).subscribe(
