import { TokenService } from './../services/token.service';
import { ModalManager } from 'ngb-modal';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { PoleAndPerimetreService } from './../services/pole-and-perimetre.service';
import { Component, OnInit } from '@angular/core';
import { NgbModal } from "@ng-bootstrap/ng-bootstrap";
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-module-skills-matrix',
  templateUrl: './module-skills-matrix.component.html',
  styleUrls: ['./module-skills-matrix.component.css']
})
export class ModuleSkillsMatrixComponent implements OnInit {

  constructor(
    public poleAndPerimetreService: PoleAndPerimetreService,
    private modalManager: ModalManager,
    private modalService: NgbModal,
    public tokenService: TokenService,
    private http: HttpClient,

  ) { }
message:string;
  role: any;
  dataa = {
    "titre": "",
    "texte": "",
    "path": ""
  }
  ngOnInit(): void {
    this.tokenService.printRoles()
    if (this.tokenService.getToken() != null) {
      if (this.tokenService.isAdmin() == true) {
        this.role = "admin";
        this.getPoles();
      }
      else if (this.tokenService.isConsultant() == true) {
        this.role = "consultant";
        this.poles = [
          { name: '' },
        ]
        this.poles[0].name = this.tokenService.getPole();
      }
      else if (this.tokenService.isTeamManager() == true) {
        this.role = "Team-manager";
        this.poles = [
          { name: '' },
        ]
        this.poles[0].name = this.tokenService.getPole();
      }
      else if (this.tokenService.isSpecialiste() == true)
        this.role = "specialiste";
      this.poles = [
        { name: '' },
      ]
      this.poles[0].name = this.tokenService.getPole();

    }
  }
  addForm = new FormGroup({
    name: new FormControl('', [Validators.required]),
    path: new FormControl('', [Validators.required]),
  })

  editForm = new FormGroup({
    name: new FormControl('', [Validators.required]),
    path: new FormControl('', [Validators.required]),
  })

  poles: any;

  public onFileChanged(event: any) {
    //Select File
    this.selectedFile = event.target.files[0];
  }

  getPoles() {
    this.poleAndPerimetreService.getPoles().subscribe(

      (res) => {
        this.poles = res;
      }
      ,
      (_err) => {

      });
  }
  data = {
    name: ""
  };
  addPole() {
    this.data.name = this.addForm.value.name!;
    this.addForm.value.name = "";
    console.log("state " + this.addForm.value.name)
    this.poleAndPerimetreService.addPole(this.data).subscribe(
      (_res) => {
        this.poles = this.getPoles();
        this.close()
      },
      (_err) => { }
    )

  }

  private modalRef = {};

  open(content: any) {
    this.modalRef = this.modalService.open(content, { centered: true });
    console.log(this.modalRef);
  }

  close() {
    this.modalManager.close(this.modalRef);
    this.poleToDel = "";
  }


  poleToDel: any;

  open2(content: any, pole: any) {
    this.poleToDel = pole;
    this.modalRef = this.modalService.open(content, { centered: true });
    console.log(this.modalRef);
  }

  deletePole() {
    this.poleAndPerimetreService.deletePole(this.poleToDel).subscribe(
      (_res) => {
        this.poles = this.getPoles();
        this.close()
      },
      (_err) => { }
    )
  }
  selectedFile:File;
  onUpload() {

    //FormData API provides methods and properties to allow us easily prepare form data to be sent with POST HTTP requests.
    const uploadImageData = new FormData();
    uploadImageData.append('imageFile', this.selectedFile, this.selectedFile.name);
    this.dataa.path = this.selectedFile.name!

    //Make a call to the Spring Boot Application to save the image
    this.http.post('http://localhost:8081/image/upload', uploadImageData, { observe: 'response' })
      .subscribe((response) => {
        if (response.status === 200) {
          this.message = 'Image uploaded successfully';
        } else {
          this.message = 'Image not uploaded successfully';
        }
      }
      );

  }

  editPole() {
    this.poleAndPerimetreService.editPole(this.poleToDel, this.editForm.value.name).subscribe(
      (_res) => {
        this.poles = this.getPoles();
        this.close()
      },
      (_err) => { }
    )
  }
}
<body>

  <div class="w3-show-inline-block">

    <button mat-fab color="primary" class="mt-4 ml-4  float-left" [routerLink]="['/home']">
      <mat-icon>arrow_back_ios</mat-icon>
    </button>

    <button mat-fab color="primary" class="mt-4 ml-4 float-right" (click)="open(popup)"  *ngIf="role=='Team-manager' || role=='admin'">
      <mat-icon>add</mat-icon>
    </button>

  </div>
  <div class="container">

    <div class="row">
      <div class="" *ngFor="let item of categories$ |async  ">


      </div>
    </div>
  </div>
<!-- me -->
  <section class="mini " id="work-process">
    <div class="mini-content">
      <div class="container">
        <div class="row">
          <div class="col-lg-3" *ngFor="let pole of poles">
            <a href="#" [href]="'/Pole/Perimetre?name='+ pole.name">
            <div class="card w-2000" >
              <img class="card-img-top" [src]="assets/pole.jpeg" alt="Card image cap">
              <div class="card-body">
                <h2 class="card-title text-center font-weight-bold"><button class="router" (click)="getCoursesbyid(item.id)">{{item.titre}}</button></h2>
                <p class="card-text">{{pole.name}}</p>
               </div>
              </div>
            </a>
            <button type="button" class="btn btn-outline-warning" id="warn" *ngIf="role!='consultant'"
                (click)="open2(edit,pole.name)">
                <i class="fa-solid fa-pen" id="icon"></i> </button>
              <button type="button" class="btn btn-danger" *ngIf="role!='consultant'" (click)="open2(delete,pole.name)">
                <i class="fa-solid fa-trash-can" id="icon"></i>
              </button>
            </div> 
            <!-- <div class="mini-box">
              <a href="#" [href]="'/Pole/Perimetre?name='+ pole.name">
                <i><img class="imageresize" src="assets/pole.jpeg" alt=""></i>
                <strong>{{pole.name}}</strong>
              </a>
              <button type="button" class="btn btn-outline-warning" id="warn" *ngIf="role!='consultant'"
                (click)="open2(edit,pole.name)">
                <i class="fa-solid fa-pen" id="icon"></i> </button>
              <button type="button" class="btn btn-danger" *ngIf="role!='consultant'" (click)="open2(delete,pole.name)">
                <i class="fa-solid fa-trash-can" id="icon"></i>
              </button>

            </div> -->
          </div>
        </div>

    </div>
  </section>
<!-- me -->
</body>

<footer>
  <app-footer></app-footer>
</footer>
<ng-template #popup let-c="close" let-d="dismiss">
  <div class="modal-header">
    <h4 class="modal-title"></h4>

    <button type="button" class="close" aria-label="Close" id="cross" (click)="d('Cross click')">
      <i class="fas fa-times" id="crossC"></i>
    </button>
  </div>
  <div class="modal-body">

    <form class="form-group" [formGroup]="addForm">
      <label for="name">Name <span style="color:red">*</span></label>
      <input class="form-control" type="text" placeholder="Name ..." id="name" formControlName="name">
      <br>
      <input formControlName="path" type="file" accept="image/*" class="form-control" id="customFile"
      (change)="onFileChanged($event)" value={{addForm.value.path}} />
    </form>
  </div>
  <div class="modal-footer">
    <button type="button" class="btn btn-outline-dark" id="cancel" (click)="close()">
      Cancel
    </button>
    <button type="button" ngbAutofocus class="btn btn-outline-primary" id="add" (click)="addPole()">
      Add
    </button>
  </div>
</ng-template>

<ng-template #delete let-c="close" let-d="dismiss">
  <div class="modal-header">
    <h4 class="modal-title"></h4>

    <button type="button" class="close" aria-label="Close" id="cross" (click)="d('Cross click')">
      <i class="fas fa-times" id="crossC"></i>
    </button>
  </div>
  <div class="modal-body">
    <p>Are you sure you want to delete
      <span id="username">{{poleToDel}}</span> ?
    </p>
  </div>
  <div class="modal-footer">
    <button type="button" ngbAutofocus class="btn btn-outline-dark" id="cancel" (click)="close()">
      Cancel
    </button>
    <button type="button" class="btn btn-outline-warning" id="delete" (click)="deletePole()">
      Delete
    </button>
  </div>
</ng-template>

<ng-template #edit let-c="close" let-d="dismiss">
  <div class="modal-header">
    <h4 class="modal-title"></h4>

    <button type="button" class="close" aria-label="Close" id="cross" (click)="d('Cross click')">
      <i class="fas fa-times" id="crossC"></i>
    </button>
  </div>
  <div class="modal-body">

    <form class="form-group" [formGroup]="editForm">
      <label for="name">Name <span style="color:red">*</span></label>
      <input class="form-control" type="text" placeholder="Name ..." id="name" formControlName="name">
    </form>
  </div>
  <div class="modal-footer">
    <button type="button" class="btn btn-outline-dark" id="cancel" (click)="close()">
      Cancel
    </button>
    <button type="button" ngbAutofocus class="btn btn-outline-primary" id="warn" (click)="editPole()">
      Edit
    </button>
  </div>
</ng-template>
