import { Question } from './../../src-api/question/model';
import { MatriceDeCompetencesService } from './../services/matrice-de-competences.service';
import { ModalManager } from 'ngb-modal';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { Component, OnInit } from '@angular/core';

import { AddConsultantComponent } from "../add-consultant/add-consultant.component";
import { NgbModal } from "@ng-bootstrap/ng-bootstrap";
import { AddCompetenceComponent } from "../add-competence/add-competence.component";
import { DataService } from "../../src-api/Data";
import { Perimetre } from "../../src-api/perimetre/model";
import { AddCategoryComponent } from "../add-category/add-category.component";
import * as XLSX from "xlsx";



@Component({
  selector: 'app-details-skills-matrix',
  templateUrl: './details-skills-matrix.component.html',
  styleUrls: ['./details-skills-matrix.component.css']
})



export class DetailsSkillsMatrixComponent implements OnInit {

  constructor(private modalService: NgbModal,
    public data: DataService,
    private route: ActivatedRoute,
    private modalManager: ModalManager,
    private matriceDeCompetencesService: MatriceDeCompetencesService

  ) {
  }
  addFormCat = new FormGroup({
    name: new FormControl('', [Validators.required])
  })

  addFormSkill = new FormGroup({
    name: new FormControl('', [Validators.required]),
    nvc: new FormControl('1', [Validators.required]),
    nvm: new FormControl('1', [Validators.required]),
    cat: new FormControl('', [Validators.required]),
    criticite: new FormControl('1', [Validators.required]),

  })

  editFormSkill = new FormGroup({
    name: new FormControl('', [Validators.required]),
    nvc: new FormControl('', [Validators.required]),
    nvm: new FormControl('', [Validators.required]),
    cat: new FormControl('', [Validators.required]),
    criticite: new FormControl('1', [Validators.required]),

  })

  addQuestionForm = new FormGroup({
    quest: new FormControl('', [Validators.required]),
    rep1Correcte: new FormControl('', [Validators.required]),
    rep2: new FormControl('', [Validators.required]),
    rep3: new FormControl('', [Validators.required]),
    rep4: new FormControl('', [Validators.required]),

  })

  Pole: any;
  Perimetre: any;
  Matrixe: any;

  ngOnInit(): void {
    this.Pole = this.route.snapshot.queryParamMap.get("pole")!
    this.Perimetre = this.route.snapshot.queryParamMap.get("perimetre")!
    this.Matrixe = this.route.snapshot.queryParamMap.get("name")!
    this.getCategories();
  }
  dataAddCat = {
    "name": "",
    "matrixe": ""
  }
  private modalRef = {};

  open(content: any) {
    this.modalRef = this.modalService.open(content, { centered: true });
    console.log(this.modalRef);
  }

  skillForQuest: any;
  cpt: any = 1;
  print() {

  }
  open2(content: any, skill: any) {
    this.skillForQuest = skill;
    this.dataSkill.name = this.skillForQuest.name!;
    this.dataSkill.categorie = this.skillForQuest.categorie;
    this.editFormSkill.get('name')?.setValue(this.skillForQuest.name)
    this.editFormSkill.get('nvc')?.setValue(this.skillForQuest.niveauCible)
    this.editFormSkill.get('nvm')?.setValue(this.skillForQuest.niveauMin)
    this.editFormSkill.get('criticite')?.setValue(this.skillForQuest.criticite)
    console.log("shuf" + JSON.stringify(this.skillForQuest))

    this.modalRef = this.modalService.open(content, { centered: true });
    console.log(this.modalRef);
  }
  async delay() {
    return new Promise(resolve => setTimeout(resolve, 500));
  }
  async nextOpen(content: any) {
    this.addQuestion();
    this.close()
    await this.delay();
    this.open2("addQuestio", this.skillForQuest.name);
    this.cpt++;
    this.modalRef = this.modalService.open(content, { centered: true });
    console.log(this.modalRef);
  }
  close() {
    this.modalManager.close(this.modalRef);

  }
  question = {
    "skill": "",
    "quest": "",
    "rep1Correcte": "",
    "rep2": "",
    "rep3": "",
    "rep4": ""
  };

  reset() {
    this.addQuestionForm.get('quest')?.setValue("")
    this.addQuestionForm.get('rep1Correcte')?.setValue("")
    this.addQuestionForm.get('rep2')?.setValue("")
    this.addQuestionForm.get('rep3')?.setValue("")
    this.addQuestionForm.get('rep4')?.setValue("")
  }
  addQuestDone() {
    this.addQuestion();
    this.skillForQuest = null;
    this.cpt = 1;
    this.close();

  }
  addQuestion() {
    console.log("shuf 11" + JSON.stringify(this.skillForQuest));

    this.question.skill = this.skillForQuest.name;
    this.question.quest = this.addQuestionForm.value.quest!;
    this.question.rep1Correcte = this.addQuestionForm.value.rep1Correcte!;
    this.question.rep2 = this.addQuestionForm.value.rep2!;
    this.question.rep3 = this.addQuestionForm.value.rep3!;
    this.question.rep4 = this.addQuestionForm.value.rep4!;

    this.matriceDeCompetencesService.addQuestion(this.question).subscribe(
      (res) => {
        this.reset()
      },
      (err) => { }
    )
  }

  deleteAllQuest() {
    this.matriceDeCompetencesService.deleteQuestions(this.skillForQuest.name).subscribe(
      (res) => {
        this.reset()
        this.close()
      },
      (err) => { }
    )
  }

  editSki() {
    this.dataSkill.newName = this.editFormSkill.value.name!;
    this.dataSkill.criticite = this.editFormSkill.value.criticite!;
    this.dataSkill.niveauMin = this.editFormSkill.value.nvm!;
    this.dataSkill.niveauCible = this.editFormSkill.value.nvc!;
    console.log("shuf 2" + JSON.stringify(this.dataSkill));

    this.matriceDeCompetencesService.editSkill(this.dataSkill).subscribe(
      (res) => {
        this.getCategories();
        this.close()
      }
    )
  }
  deleteSki(skill: any) {
    this.matriceDeCompetencesService.deleteSkill(skill).subscribe(
      (res) => {
        this.getCategories();
        this.close()
      }
    )
  }
  addCategory() {

    this.dataAddCat.name = this.addFormCat.value.name!;
    this.dataAddCat.matrixe = this.Matrixe;
    this.addFormCat.value.name = "";
    console.log(JSON.stringify(this.dataAddCat))
    this.matriceDeCompetencesService.addCategorie(this.dataAddCat).subscribe(
      (res) => {
        this.categories = this.getCategories();
        this.close()
      },
      (err) => { }
    )
  }

  dataSkill = {
    newName: "",
    name: "",
    criticite: "",
    niveauMin: "",
    niveauCible: "",
    categorie: ""
  }

  addSki() {

    this.dataSkill.name = this.addFormSkill.value.name!;
    this.dataSkill.criticite = this.addFormSkill.value.criticite!;
    this.dataSkill.niveauMin = this.addFormSkill.value.nvm!;
    this.dataSkill.categorie = this.addFormSkill.value.cat!;
    this.dataSkill.niveauCible = this.addFormSkill.value.nvc!;

    console.log(this.dataSkill)
    this.matriceDeCompetencesService.addSkill(this.dataSkill).subscribe(
      (res) => {
        this.getCategories();
        this.close()
      }
    )

  }
  categories: any;

  getSkills(): any {
    for (let i = 0; i < this.categories.length; i++) {

      this.matriceDeCompetencesService.getSkills(this.categories[i].name).subscribe(
        (res) => {
          this.categories[i].skills = res
          console.log("lol" + res)
          return res;
        },
        (err) => { }
      )
    }
  }
  name: any;
  getCategories() {
    /*this.matriceDeCompetencesService.getCategories(this.Matrixe).subscribe(
      (res) => {
        this.categories = res;
        this.close()
      },
      (err) => { }
    )
    this.matriceDeCompetencesService.getCategories(this.Matrixe).subscribe(
      (res) => {
        this.categories = res;
      }
    )
    for (let i = 0; i < this.categories.length; i++) {
      this.categories[i].skills = this.getSkills(this.categories[i].name);
    }*/
    this.matriceDeCompetencesService.getCategories(this.Matrixe).subscribe((result) => {
      // wait for the async response
      this.categories = result;
      this.getSkills();
      console.log('I get some data from asyncCall()');
    });
    // sync code executed, even if the previous async call doesn't respond anything
    console.log('Code executed after the subscription. But not waiting for it to respond');
  }


  perimetres = this.data.perimetres;

  fileName = "SkillsMatrixsheet.xlsx";


  dataoutils: Competence[] = [
    { NomConsultant: 'Consultant1', CatiaV5: 4, VLM: 3, TeamCenter: 0 },
    { NomConsultant: 'Consultant2', CatiaV5: 1, VLM: 4, TeamCenter: 1 },
    { NomConsultant: 'Consultant3', CatiaV5: 0, VLM: 4, TeamCenter: 2 },
    { NomConsultant: 'Consultant4', CatiaV5: 3, VLM: 4, TeamCenter: 1 },
  ];

  datacom: Competence1[] = [
    { NomConsultant: 'Consultant1', Francais: 4, Anglais: 3, Allemand: 0, critique: 'N' },
    { NomConsultant: 'Consultant2', Francais: 1, Anglais: 4, Allemand: 1 },
    { NomConsultant: 'Consultant3', Francais: 0, Anglais: 4, Allemand: 2 },
    { NomConsultant: 'Consultant4', Francais: 3, Anglais: 4, Allemand: 1 },
  ];

  displayedColumns =
    ['IDconsultant', 'Pole', 'Activiteactuelle', 'natureactivite', 'IDactivite', 'TLSp', 'NomConsultant'];
  dataSource: Personne[] = [
    {
      IDconsultant: '1',
      Pole: 'DMA',
      Activiteactuelle: 'activité1',
      natureactivite: 'nature activite',
      IDactivite: '1',
      TLSp: 'superieur',
      NomConsultant: 'Consultant1',
      competence: this.dataoutils
    },
    {
      IDconsultant: '2',
      Pole: 'DMA',
      Activiteactuelle: 'activité1',
      natureactivite: 'nature activite',
      IDactivite: '1',
      TLSp: 'superieur',
      NomConsultant: 'Consultant2',
      competence: this.dataoutils
    },
    {
      IDconsultant: '3',
      Pole: 'DMA',
      Activiteactuelle: 'activité1',
      natureactivite: 'nature activite',
      IDactivite: '1',
      TLSp: 'superieur',
      NomConsultant: 'Consultant3',
      competence: this.dataoutils
    },
    {
      IDconsultant: '4',
      Pole: 'DMA',
      Activiteactuelle: 'activité1',
      natureactivite: 'nature activite',
      IDactivite: '1',
      TLSp: 'superieur',
      NomConsultant: 'Consultant4',
      competence: this.dataoutils
    },

  ];

  columnsToDisplay: string[] = this.displayedColumns.slice();

  outilsColumns =
    [' ', 'CatiaV5', 'VLM', 'TeamCenter'];
  columnsoutilsToDisplay: string[] = this.outilsColumns.slice();

  communicationColumns =
    [' ', 'Francais', 'Anglais', 'Allemand'];
  columnscomToDisplay: string[] = this.communicationColumns.slice();


  skills: skill[] =
    [
      { critique: 'O', NiveauMin: 1, NiveauCible: 2 },
      { critique: 'O', NiveauMin: 2, NiveauCible: 3 },
      { critique: 'N', NiveauMin: 1, NiveauCible: 3 }
    ]


  folders: Section[] = [
    {
      name: 'Trimestre 1',
      updated: new Date('1/1/21'),
    },
    {
      name: 'Trimestre 2',
      updated: new Date('5/1/21'),
    },
    {
      name: 'Trimestre 3',
      updated: new Date('9/1/21'),
    }
  ];


  addConsultant() {

    this.modalService.open(AddConsultantComponent);

  }

  addCompetence() {

    this.modalService.open(AddCompetenceComponent);

  }


  wb: XLSX.WorkBook = XLSX.utils.book_new();
  i = 1;
  addToExcel(): void {

    let element = document.getElementById('infos-excel');
    const ws: XLSX.WorkSheet = XLSX.utils.table_to_sheet(element);
    XLSX.utils.book_append_sheet(this.wb, ws, 'Sheet' + this.i);
    this.i = this.i + 1;
  }

  exportExcel(): void {
    XLSX.writeFile(this.wb, this.fileName);
  }

}

export interface Personne {
  NomConsultant?: string;
  Pole?: string;
  Activiteactuelle?: string;
  natureactivite?: string;
  IDactivite?: string;
  TLSp?: string;
  IDconsultant?: string;
  competence: Competence[];
}

export interface Category {
  NameCategory: string;
  Competence: Competence[];
}

export interface Competence {
  NomConsultant?: string;
  critique?: string;
  NiveauMin?: number;
  NiveauCible?: number;
  CatiaV5?: number;
  VLM?: number;
  TeamCenter: number;
}
export interface Competence1 {
  NomConsultant?: string;
  critique?: string;
  NiveauMin?: number;
  NiveauCible?: number;
  Francais?: number;
  Anglais?: number;
  Allemand: number;
}
export interface skill {
  critique?: string;
  NiveauMin?: number;
  NiveauCible?: number;
}
export interface Section {
  name: string;
  updated: Date;
}


<div>
  <div class="image-back">
    <div class="return">


      <a mat-fab color="primary" class="mt-4 ml-4 "
        [href]="'/Pole/Perimetre/SkillsMatrix?name='+Perimetre+'&pole='+Pole">
        <!--[routerLink]="['./..']"-->
        <mat-icon>arrow_back_ios</mat-icon>
      </a>
    </div>

    <br><br><br><br><br><br><br><br><br><br>
    <div class="container">
      <div class="col-md-12 text-center">
        <button id="buttona" mat-raised-button color="primary" (click)="open(addSkill)"> Ajouter competence </button>
        <button id="buttona" mat-raised-button color="accent" (click)="open(addCat)"> Ajouter Categorie </button>
        <button id="buttona" mat-raised-button color="warn" (click)="open(addCat)"> Supprimer Categorie </button>
      </div>
    </div>
    <!-- <button id="buttona" mat-raised-button color="primary" (click)="addConsultant()"> Ajouter consultant </button>-->

  </div>
  <mat-tab-group animationDuration="0ms">
    <mat-tab label="Infos">
      <div class="example-container mat-elevation-z8">

        <table id="infos-excel" class="table table-striped">
          <thead>
            <tr>
              <th scope="col" colspan="7" class="items-center">Competences</th>
            </tr>
            <tr>
              <th scope="col" *ngFor="let a of displayedColumns.slice(1,displayedColumns.length)"> </th>
              <th scope="row">criticite</th>
            </tr>
            <tr>
              <th scope="col" *ngFor="let a of displayedColumns.slice(1,displayedColumns.length)"> </th>
              <th scope="row">Niveau minimum</th>
            </tr>
            <tr>
              <th scope="col" *ngFor="let a of displayedColumns.slice(1,displayedColumns.length)"> </th>
              <th scope="row">Niveau cible</th>
            </tr>
            <tr>
              <th *ngFor="let column of displayedColumns" scope="col" class="col-tab">{{column}}</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let consultant of data.consultants">
              <td>{{consultant.idUser}}</td>
              <td>{{consultant.pole}}</td>
              <td>{{consultant.activiteActuelle}}</td>
              <td>{{consultant.natureActivite}}</td>
              <td>{{consultant.idActivite}}</td>
              <td>{{consultant.userType}}</td>
              <td>{{consultant.fullname}}</td>
            </tr>
          </tbody>
        </table>
      </div>

    </mat-tab>

    <mat-tab *ngFor="let category of categories" label={{category.name}}>
      <div class="example-container mat-elevation-z8">
        <table id="infos-excel" class="table table-striped">
          <thead>
            <tr>
              <th scope="col" class="col-tab"></th>
              <th *ngFor="let skill of category.skills" scope="col" class="col-tab">
                <a routerLink="/Modulequiz/Responsablequiz/Quizzes/Detailsquiz">{{skill.name}}</a>
                <button id="but" class="m-0 float-right mx-3" (click)="open2(editSkill,skill)">
                  <i id="icon" class="fa-solid fa-pencil"></i> </button>
                <button id="but" class="m-0 float-right mx-3" (click)="open2(addQuestio,skill)">
                  <i id="icon" class="fa-solid fa-plus"></i> </button>
                <button id="but" class="m-0 float-right mx-2" (click)="open2(deleteSkill,skill)">
                  <i id="icon" class="fa-solid fa-trash-can"></i> </button>
              </th>
            </tr>
            <tr>
              <th scope="row">Criticite</th>
              <td *ngFor="let skill of category.skills">{{skill.criticite}}</td>
            </tr>
            <tr>
              <th scope="row">Niveau minimum</th>
              <td *ngFor="let skill of category.skills">{{skill.niveauMin}}</td>
            </tr>
            <tr>
              <th scope="row">Niveau cible</th>
              <td *ngFor="let skill of category.skills">{{skill.niveauCible}}</td>
            </tr>
            <tr>
              <th scope="col" colspan="10" class="col-tab">Nom Consultant</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let consultant of data.consultants">
              <td>{{consultant.fullname}}</td>
              <td *ngFor="let skill of category.skills">{{skill.note}}</td>
            </tr>
          </tbody>
        </table>
      </div>
    </mat-tab>
  </mat-tab-group>
  <!--<button mat-stroked-button color="primary" (click)="addToExcel()">Add to excel</button>-->
  <button mat-stroked-button color="primary" id="buttona" (click)="exportExcel()">Export excel</button>

  <mat-list>
    <div mat-subheader>Folders</div>

    <mat-list-item *ngFor="let folder of folders">
      <mat-icon mat-list-icon>folder</mat-icon>
      <div mat-line>{{folder.name}}</div>
      <div mat-line> {{folder.updated }} </div>

    </mat-list-item>
  </mat-list>


</div>

<app-footer></app-footer>

<ng-template #addCat let-c="close" let-d="dismiss">
  <div class="modal-header">
    <h4 class="modal-title">Add Categorie</h4>

    <button type="button" class="close" aria-label="Close" id="cross" (click)="d('Cross click')">
      <i class="fas fa-times" id="crossC"></i>
    </button>
  </div>
  <div class="modal-body">

    <form class="form-group" [formGroup]="addFormCat">
      <label for="name">Name <span style="color:red">*</span></label>
      <input class="form-control" type="text" placeholder="Name ..." id="name" formControlName="name">
    </form>
  </div>
  <div class="modal-footer">
    <button type="button" class="btn btn-outline-dark" id="cancel" (click)="close()">
      Cancel
    </button>
    <button type="button" ngbAutofocus class="btn btn-outline-primary" id="add" (click)="addCategory()">
      Add
    </button>
  </div>
</ng-template>

<ng-template #addSkill let-c="close" let-d="dismiss">
  <div class="modal-header">
    <h4 class="modal-title">Add Skill</h4>

    <button type="button" class="close" aria-label="Close" id="cross" (click)="d('Cross click')">
      <i class="fas fa-times" id="crossC"></i>
    </button>
  </div>
  <div class="modal-body">

    <form class="form-group" [formGroup]="addFormSkill">
      <label for="name">Name <span style="color:red">*</span></label>
      <input class="form-control" type="text" placeholder="Name ..." id="name" formControlName="name">
      <br>

      <div class="form-group">
        <label for="categ">Categories *</label>
        <select class="form-control" id="categ" aria-label="Default select example" formControlName="cat"
          value="{{addFormSkill.value.cat}}">
          <option>choose one</option>
          <option *ngFor="let category of categories">{{category.name}}
          </option>
        </select>
      </div>
      <br>
      <div class="d-flex flex-row align-items-center mb-4">
        <span class="mx-5"></span>
        <div class="form-group col-md-2 ">
          <label class=" form-label" for="criticite">Criticité</label>

          <input id="criticite" formControlName="criticite" type="number" class="form-control col-md-2" value="1"
            min="0" max="10" step="1" />

        </div>

        <div class="form-group col-md-2 mx-4">
          <label class=" form-label" for="nvm">Niveau Min</label>

          <input id="nvm" formControlName="nvm" type="number" class="form-control col-md-2" value="1" min="0" max="10"
            step="1" />

        </div>

        <div class="form-group col-md-2 mx-3">
          <label class=" form-label" for="nvc">Niveau Cible</label>

          <input id="nvc" formControlName="nvc" type="number" class="form-control col-md-2" value="1" min="0" max="10"
            step="1" />

        </div>

      </div>

    </form>
  </div>
  <div class="modal-footer">
    <button type="button" class="btn btn-outline-dark" id="cancel" (click)="close()">
      Cancel
    </button>
    <button type="button" ngbAutofocus class="btn btn-outline-primary" id="add" (click)="addSki()">
      Add
    </button>
  </div>
</ng-template>

<ng-template #addQuestio let-c="close" let-d="dismiss">
  <div class="modal-header">
    <h4 class="modal-title">Add Question {{cpt}} for : <b><em style="color:blueviolet">{{skillForQuest.name}}</em></b>
    </h4>


    <button type="button" class="close" aria-label="Close" id="cross" (click)="d('Cross click')">
      <i class="fas fa-times" id="crossC"></i>
    </button>
  </div>
  <div class="modal-body">
    <p style="color:red"><em> <i class="fa-solid fa-triangle-exclamation"></i> as soon as you go to the next question
        the
        previous one is already
        created</em></p>
    <form class="form-group" [formGroup]="addQuestionForm">
      <label for="quest">Question <span style="color:red">*</span></label>
      <input class="form-control" type="text" placeholder="..." id="quest" formControlName="quest">
      <br>
      <label for="rep1Correcte">Correct Answer <span style="color:red">*</span></label>
      <input class="form-control" type="text" placeholder="..." id="rep1Correcte" formControlName="rep1Correcte">
      <br>
      <label for="rep2">Second Choice <span style="color:red">*</span></label>
      <input class="form-control" type="text" placeholder="..." id="rep2" formControlName="rep2">
      <br>
      <label for="rep3">3th Choice <span style="color:green">(opt)</span></label>
      <input class="form-control" type="text" placeholder="..." id="rep3" formControlName="rep3">
      <br>
      <label for="rep4">4th Choice <span style="color:green">(opt)</span></label>
      <input class="form-control" type="text" placeholder="..." id="rep4" formControlName="rep4">
    </form>
  </div>
  <div class="modal-footer">
    <button type="button" class="btn btn-outline-dark" id="cancel" (click)="deleteAllQuest()">
      Cancel
    </button>
    <button type="button" class="btn btn-outline-primary" id="next" (click)="nextOpen(addQuestio)">
      Next
    </button>
    <button type="button" ngbAutofocus class="btn btn-outline-warning" id="warn" (click)="addQuestDone()">
      Done
    </button>
  </div>
</ng-template>

<ng-template #editSkill let-c="close" let-d="dismiss">
  <div class="modal-header">
    <h4 class="modal-title">Add Skill</h4>

    <button type="button" class="close" aria-label="Close" id="cross" (click)="d('Cross click')">
      <i class="fas fa-times" id="crossC"></i>
    </button>
  </div>
  <div class="modal-body">

    <form class="form-group" [formGroup]="editFormSkill">
      <label for="name">Name <span style="color:red">*</span></label>
      <input class="form-control" type="text" placeholder="Name ..." id="name" formControlName="name">
      <br>
      <div class="d-flex flex-row align-items-center mb-4">
        <span class="mx-5"></span>
        <div class="form-group col-md-2 ">
          <label class=" form-label" for="criticite">Criticité</label>

          <input id="criticite" formControlName="criticite" type="number" class="form-control col-md-2" min="0" max="10"
            step="1" />

        </div>

        <div class="form-group col-md-2 mx-4">
          <label class=" form-label" for="nvm">Niveau Min</label>

          <input id="nvm" formControlName="nvm" type="number" class="form-control col-md-2" min="0" max="10" step="1" />

        </div>

        <div class="form-group col-md-2 mx-3">
          <label class=" form-label" for="nvc">Niveau Cible</label>
          <input id="nvc" formControlName="nvc" type="number" class="form-control col-md-2" min="0" max="10" step="1" />

        </div>

      </div>

    </form>
  </div>
  <div class="modal-footer">
    <button type="button" class="btn btn-outline-dark" id="cancel" (click)="close()">
      Cancel
    </button>
    <button type="button" ngbAutofocus class="btn btn-outline-primary" id="add" (click)="editSki()">
      Edit
    </button>
  </div>
</ng-template>


<ng-template #deleteSkill let-c="close" let-d="dismiss">
  <div class="modal-header">
    <h4 class="modal-title">Add Skill</h4>

    <button type="button" class="close" aria-label="Close" id="cross" (click)="d('Cross click')">
      <i class="fas fa-times" id="crossC"></i>
    </button>
  </div>
  <div class="modal-body">
    <p>Are you sure you want to accept
      <em id="username">{{skillForQuest.name}}</em> demand ?
    </p>
  </div>
  <div class="modal-footer">
    <button type="button" class="btn btn-outline-dark" id="cancel" (click)="close()">
      Cancel
    </button>
    <button type="button" ngbAutofocus class="btn btn-outline-danger" id="delete"
      (click)="deleteSki(skillForQuest.name)">
      Edit
    </button>
  </div>
</ng-template>
